import { createContext, useState } from "react"

const CartContex = createContext([])


const CartProvider = ({children}) =>{

  const [productoAregadoAlCarrito,setProductoAregadoAlCarrito]= useState({})

    return(

    <CartContex.Provider value={[

        productoAregadoAlCarrito,
        setProductoAregadoAlCarrito
    ]}

    >

        {children}
    </CartContex.Provider>

    )
}

export{CartProvider}
