import { useContext } from "react";
import { CartContex } from "./CartContex";
import Item from "../Item/Item";



const Cart = () => {

    const { addProduct, lista,cantida} = useContext(CartContex)


    /* 
          {lista.map((productomapeado) => (

         <Item producto={productomapeado} key={productomapeado.id}/>



        ))}
    */

        const precioTotal = lista.reduce((accum,producto)=> accum + producto.precio, 0)


    return (


        <>
            <p>CARRITO {cantida}</p>


            {lista.map((productomapeado) => (

         <Item producto={productomapeado} key={productomapeado.id}/>



        ))}

      <div>{precioTotal}</div>

        </>
    )

}

export { Cart }
