import { getProducts,getProduct } from "../../services/Productos"
import { ItemDetail } from "../ItemDetail/ItemDetail"
import { useParams } from "react-router-dom"

import { getProductsFireB} from "../../services/Firebase/Firebase";

 const ItemDetailContainer = () =>{

  const {id}= useParams();


  //////NUEVO

  useEffect(() => {

    getProductsFireB(id).then((res)=>{

      setProductosFireB(res)

    })
   }
    , [id]);



  //////




  const [productoDetallado,setproductoDetallado]= useState()

  useEffect ( ()=> {

    getProduct(id)
    .then((res)=>setproductoDetallado(res))

    console.log(id);



  },[])


   if(!productoDetallado)
      return "cargando"



    return(

       <>


       <ItemDetail productoDetallado={productoDetallado}/>



        </>


    )



}


export {ItemDetailContainer};


