          <h5 class="card-title">{nombre}</h5>
          <p class="card-text">{descripcion}</p>
          <p class="card-text">${precio}</p>
          <p class="card-text">stock: {cantidad}</p>
          <button  class="btn btn-light" onClick={onProductClicked}>Ver m√°s</button>
          </div>

        </div>


      </>
    );
  };

  export default Item;
